
public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n=24;
		if(n%2==0 && n>2&&n<5)
			System.out.println("\n print not weird");
		else if(n%2==0 && n>2&&n<20)
			System.out.println("\n print  weird");
		if(n%2==0 && n>20)
			System.out.println("\n print not weird");
		else if(n%2!=0)
			System.out.println("\n print  weird");
		
	}

}
